script:
function renderMap(aircrafts) {
  let mapContainer = root_element.querySelector("#aircraft-locations-map");
  if (!mapContainer) {
    console.error("Map container not found!");
    return;
  }

  // Initialize the map with a center and zoom level
  let map = L.map(mapContainer).setView([0, 0], 2); // Set initial center and zoom

  let tileLayerSatellite = L.tileLayer(
    "https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}",
    {
      attribution:
        "Tiles &copy; Esri &mdash; Source: Esri, i-cubed, USDA, USGS, AEX, GeoEye, Getmapping, Aerogrid, IGN, IGP, UPR-EGP, and the GIS User Community",
    }
  );

  let tileLayerTerrain = L.tileLayer(
    "https://server.arcgisonline.com/ArcGIS/rest/services/NatGeo_World_Map/MapServer/tile/{z}/{y}/{x}",
    {
      attribution:
        "Tiles &copy; Esri &mdash; National Geographic, Esri, DeLorme, NAVTEQ, UNEP-WCMC, USGS, NASA, ESA, METI, NRCAN, GEBCO, NOAA, iPC",
      maxZoom: 16,
    }
  );

  let tileLayerOSM = L.tileLayer(
    "https://tile.openstreetmap.org/{z}/{x}/{y}.png",
    {
      maxZoom: 19,
      attribution:
        '&copy; <a href="http://www.openstreetmap.org/copyright">OpenStreetMap</a>',
    }
  );

  let baseMaps = {
    Satellite: tileLayerSatellite,
    Terrain: tileLayerTerrain,
    OpenStreetMap: tileLayerOSM,
  };

  tileLayerSatellite.addTo(map);
  L.control.layers(baseMaps).addTo(map);

  let markers = L.markerClusterGroup({
    showCoverageOnHover: false,
    iconCreateFunction: function (cluster) {
      return L.divIcon({
        html: `<div class="cluster_icon"></div>`,
        className: "",
        iconSize: [40, 40],
      });
    },
  });

  // Store the current zoom and center before updating markers
  let lastZoom = null;
  let lastCenter = null;
  let firstRender = true; // A flag to check if it's the first render

  function updateMarkers(aircrafts) {
    markers.clearLayers(); // Remove old markers

    aircrafts.forEach((aircraft) => {
      if (aircraft.last_coordinate) {
        try {
          let locationData = JSON.parse(aircraft.last_coordinate);
          console.log(locationData);
          if (locationData.geometry && locationData.geometry.type === "Point") {
            let coords = locationData.geometry.coordinates;
            let marker = L.marker([coords[1], coords[0]]).bindPopup(`
              <b>Aircraft:</b> <a href="/app/aircraft/${aircraft.name}">${aircraft.name}</a><br>
              <b>User:</b> <a href="/app/user/${aircraft.aircraft_owner}">${aircraft.aircraft_owner}</a><br>
              <b>Coordinates:</b> Longitude:${locationData.geometry.coordinates[0]} Latitude:${locationData.geometry.coordinates[1]}<br>
              <b>Altitude:</b> ${locationData.properties.altitude}<br>
            `);
            markers.addLayer(marker);
          }
        } catch (error) {
          console.error(
            "Invalid location data for flight:",
            aircraft.name,
            error
          );
        }
      }
    });

    map.addLayer(markers);

    let bounds = markers.getBounds();
    if (bounds.isValid()) {
      // Only fit bounds on the first render
      if (firstRender) {
        map.fitBounds(bounds); // Fit bounds on the first render
        firstRender = false; // After the first render, don't reset
      }
    }

    // Restore the previous zoom level and center position if available
    if (lastZoom !== null && lastCenter !== null) {
      map.setView(lastCenter, lastZoom);
    }
  }

  function fetchAircrafts() {
    frappe.call({
      method: "frappe.client.get_list",
      args: {
        doctype: "Aircraft",
        fields: ["name", "aircraft_name", "last_coordinate", "aircraft_owner"],
        filters: [["is_active", "=", 1]],
        order_by: "modified desc",
        limit_page_length: 100,
      },
      callback: function (response) {
        if (response.message) {
          updateMarkers(response.message);
        }
      },
    });
  }

  fetchAircrafts(); // Initial fetch
  setInterval(fetchAircrafts, 2000); // Fetch every 5 seconds
}

frappe.require(
  [
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet.markercluster/1.5.3/leaflet.markercluster.js",
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet.markercluster/1.5.3/MarkerCluster.css",
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet.markercluster/1.5.3/MarkerCluster.Default.css",
  ],
  function () {
    frappe.call({
      method: "frappe.client.get_list",
      args: {
        doctype: "Aircraft",
        fields: ["name", "aircraft_name", "last_coordinate"],
        filters: [["is_active", "=", 1]],
        order_by: "modified desc",
        limit_page_length: 100,
      },
      callback: function (response) {
        if (response.message) {
          renderMap(response.message);
        }
      },
    });
  }
);

style:
#aircraft-locations-map {
    width: 100%;
    height: 600px;
    z-index: 0;
}

.cluster_icon {
    background-color: rgba(0, 123, 255, 0.8); 
    color: white; 
    border-radius: 50%; 
    width: 40px; 
    height: 40px; 
    display: flex; 
    align-items: center; 
    justify-content: center; 
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2);
}
